mapper.select:
SELECT idx ,title ,content ,orifilename ,assetfilename,assetfilepath ,assetfilesize ,transcodingendtime ,transcodingstarttime ,transcodingstatus ,catalogendtime ,catalogstarttime ,catalogstatus ,createtime ,createuser ,mediainfo ,genre ,delflag FROM VTTM_VIDEO_TB
display = true
ORDER BY idx desc LIMIT #{limit} OFFSET #{offset}

mapper.insert:

    SELECT NEXTVAL('SEQ_VTTM_VIDEO_TB')
    -- SELECT COALESCE(max(idx),0) + 1 FROM VTTM_VIDEO_TB

INSERT INTO VTTM_VIDEO_TB (
idx, title, content
) values (
#{idx}, #{title}, #{content}
)

mapper.update:
UPDATE VTTM_VIDEO_TB

    objectid = #{objectid}
    assetfilepath = #{assetfilepath}
    assetfilename = #{assetfilename}
    assetfilesize = #{assetfilesize}
    orifilename = #{orifilename}
    mediainfo = cast(#{mediainfo} as jsonb)
    catalogstatus = #{catalogstatus}
    catalogendtime = #{catalogendtime}
    volumewin = #{volumewin}
    volumeetc = #{volumeetc}
    display = #{display}


    idx = #{idx}
