mapper.select:
SELECT sectionid, videoid, starttimecode, startframeindex, endtimecode, endframeindex, sectionname, startshotid, endshotid FROM VTTM_SECTION_INFO_TB
videoid = #{idx}
ORDER BY startshotid, endshotid asc

SELECT sectionid, videoid, starttimecode, startframeindex, endtimecode, endframeindex, sectionname, startshotid, endshotid FROM VTTM_SECTION_INFO_TB
videoid = #{idx} AND sectionid = #{sectionid}
ORDER BY startshotid, endshotid asc

SELECT depictionid, sectionid, depiction FROM VTTM_SECTION_DEPICTION_TB
sectionid = #{sectionid} and userid = #{userid}
ORDER BY depictionid asc

mapper.update:
UPDATE VTTM_SECTION_DEPICTION_TB
SET
    depiction = #{depiction}
where depictionid = #{depictionid}

mapper.select:
SELECT questionid, questiontype, sectionid, question, answer, wrong_answer1, wrong_answer2, wrong_answer3, wrong_answer4 FROM VTTM_SECTION_QA_TB
sectionid = #{sectionid} and userid = #{userid}
ORDER BY questionid asc

SELECT questionid, questiontype, shotid, question, answer, wrong_answer1, wrong_answer2, wrong_answer3, wrong_answer4 FROM VTTM_SHOT_QA_TB
shotid = #{shotid} and userid = #{userid}
ORDER BY questionid asc

SELECT relationid, videoid, subject_sectionid, object_sectionid, relationcode, userid FROM VTTM_SECTION_RELATION_TB
videoid = #{videoid} and userid = #{userid}
ORDER BY subject_sectionid,object_sectionid asc

mapper.update:
UPDATE VTTM_SECTION_RELATION_TB
SET
    subject_sectionid = #{subject_sectionid},
    object_sectionid = #{object_sectionid},
    relationcode = #{relationcode}
where relationid = #{relationid}

mapper.select:
select
  d.depiction as description
from
  vttm_section_depiction_tb d
where
    d.sectionid = #{sectionid}
and d.userid = #{userid}
order by d.depictionid;

select videoType ,videoid ,vid ,sectionid as period_num ,shotId ,startshotid ,endshotid ,sectionname ,COALESCE(description,'') as description ,shot_contained ,idx from ( SELECT 'scene' AS videoType , A.videoid , CONCAT(substr(V.title,0, position('.' in V.title)),'_',lpad(cast(DENSE_RANK() over (order by a.startframeindex) as varchar),3,'0'),'_0000') AS vid , 1 as idx , a.sectionid , 0 as shotId , V.title , A.startshotid , A.endshotid , A.sectionname , cast(SUBSTR(A.sectionname, 0, POSITION('-' in A.sectionname)) AS int) AS ord , null AS shot_contained , C.question AS description FROM vttm_section_info_tb A INNER JOIN vttm_video_tb V ON A.videoid = V.idx LEFT OUTER JOIN vttm_section_qa_tb C ON A.sectionid = C.sectionid AND C.questiontype = 'DESC' and c.userid = #{userid} WHERE A.videoid = #{idx}
union all
SELECT 'shot' AS video_type , A.videoid , CONCAT(substr(V.title,0, position('.' in V.title)),'_',lpad(cast(DENSE_RANK() over (order by a.startframeindex) as varchar),3,'0'),'_',lpad(cast(cast(SUBSTR(A.sectionname, 0, POSITION('-' in A.sectionname)) AS int) + (B.shotid - A.startshotid) as varchar),4,'0')) AS vid , 2 as idx , a.sectionid , B.shotid , V.title , A.startshotid , A.endshotid , A.sectionname , cast(SUBSTR(A.sectionname, 0, POSITION('-' in A.sectionname)) AS int) AS ord , cast(SUBSTR(A.sectionname, 0, POSITION('-' in A.sectionname)) AS int) + (B.shotid - A.startshotid) AS shot_contained , C.question AS description FROM vttm_section_info_tb A INNER JOIN vttm_video_tb V ON A.videoid = V.idx INNER JOIN vttm_shot_tb B ON B.shotid BETWEEN A.startshotid AND A.endshotid LEFT OUTER JOIN vttm_shot_qa_tb C ON B.shotid = C.shotid AND C.questiontype = 'DESC' and c.userid = #{userid} WHERE A.videoid = #{idx} ) tb order by ord, shotid, idx

SELECT cast(SUBSTR(A.sectionname, 0, POSITION('-' in A.sectionname)) AS int) + (B.shotid - A.startshotid) AS shot_contained
FROM vttm_section_info_tb A
         INNER JOIN vttm_shot_tb B ON B.shotid BETWEEN A.startshotid AND A.endshotid
WHERE A.videoid = #{videoid}
  AND A.sectionid = #{sectionid}
ORDER BY A.sectionid ASC, B.shotid ASC
