mapper.select:
SELECT soundid, videoid, starttimecode, endtimecode, soundtype FROM VTTM_SOUND_TB
videoid = #{idx} and userid = #{userid}
ORDER BY starttimecode asc

mapper.update:
UPDATE VTTM_SOUND_TB
SET
    starttimecode = #{starttimecode},
    endtimecode = #{endtimecode},
    soundtype = #{soundtype}
where soundid = #{soundid}
