mapper.select:
SELECT
COUNT(*) AS COUNT
FROM
vttm_rep_img_tb

    and video_idx = #{repVideoId}
    and represent_img_seq =  #{repImgSeq}
    and section_idx = #{repSectionId}
    and shot_id = #{repShotid}

SELECT a.videoid as videoid, a.sceneid as sceneid, a.shotid as shotid, a.frameimgid as frameimgid, v.vtt_meta_idx as vtt_meta_idx, COALESCE(a.assetfilepath,v.assetfilepath) as assetfilepath, COALESCE(a.assetfilename,v.assetfilename) as assetfilename, a.registed_time as regTime, ( CASE WHEN v.vtt_meta_json IS NOT null THEN v.vtt_meta_json ELSE a.autojson END ) as repjson, ( CASE WHEN v.vtt_meta_json IS NOT null THEN 'true' ELSE 'false' END ) as savechk from vttm_frame_img_tb a left join vttm_meta_info_tb v on a.frameimgid = v.frameimgid and v.userid =
#{otheruserid}
#{userid}
and a.videoid = #{videoid}
and a.sceneid = #{sceneid}
and a.shotid = #{shotid}
and a.frameimgid = #{frameimgid}

mapper.insert:

    select
    COALESCE
    (
        (
            SELECT vtt_meta_idx FROM vttm_meta_info_tb
            where shotid = #{shotid}
            and frameimgid = #{frameimgid}
            and userid = #{userid}
            ), (
            SELECT COALESCE(max(vtt_meta_idx),0) + 1 FROM vttm_meta_info_tb
        )
    )

INSERT INTO vttm_meta_info_tb (vtt_meta_idx, idx, userid, shotid, vtt_meta_json, registed_time, updated_time)
INSERT INTO vttm_meta_info_tb (vtt_meta_idx, videoid, sceneid, shotid, frameimgid, userid, vtt_meta_json, registed_time)
VALUES(#{vtt_meta_idx}, #{videoid}, #{sceneid}, #{shotid}, #{frameimgid}, #{userid}, #{vtt_meta_json}, now())
ON CONFLICT (vtt_meta_idx)
DO
UPDATE
SET
vtt_meta_json = #{vtt_meta_json}

mapper.select:
SELECT vtt_meta_idx, idx as videoid, userid, vtt_meta_json, registed_time,shotid
FROM vttm_meta_info_tb
WHERE idx = #{videoid}
and shotid = #{shotid}
and frameimgid = #{frameimgid}
AND userid =


        #{otheruserid}


        #{userid}

SELECT M.VTT_META_JSON
  FROM VTTM_SHOT_TB S
 INNER JOIN VTTM_META_INFO_TB M
    ON S.VIDEOID = M.IDX AND S.SHOTID = M.SHOTID
 WHERE S.VIDEOID = #{idx}
   AND S.SHOTID = #{shotid}
   AND M.USERID = #{userid}
   AND S.DELFLAG = FALSE
 ORDER BY S.SHOTID
;

select fi.videoid, fi.sceneid, fi.shotid, fi.frameimgid, COALESCE(fi.assetfilepath,v.assetfilepath) as assetfilepath, COALESCE(fi.assetfilename,v.assetfilename) as assetfilename, (CASE WHEN v.vtt_meta_json IS NOT null THEN 'true' ELSE 'false' END ) as savechk, v.vtt_meta_idx, v.userid from vttm_frame_img_tb fi left join vttm_meta_info_tb v on fi.frameimgid = v.frameimgid and v.userid =
#{otheruserid}
#{userid}
where fi.videoid = #{videoid} and fi.shotid = #{shotid}

mapper.insert:

    select
    COALESCE
    (
        (
            SELECT vtt_meta_idx FROM vttm_meta_info_tb
            where shotid = #{shotid}
            and frameimgid = #{frameimgid}
            and userid = #{userid}
        ), (
            SELECT COALESCE(max(vtt_meta_idx),0) + 1 FROM vttm_meta_info_tb
        )
    )


INSERT INTO vttm_meta_info_tb (vtt_meta_idx, idx, shotid, frameimgid, userid, vtt_meta_json, registed_time, updated_time)
VALUES(#{vtt_meta_idx}, #{videoid}, #{shotid}, #{frameimgid}, #{userid}, #{vtt_meta_json}, now(), now())

mapper.select:
SELECT vtt_meta_idx, idx as videoid, userid, vtt_meta_json, registed_time,shotid
FROM vttm_meta_info_tb
WHERE idx = #{videoid}
and shotid = #{shotid}
and frameimgid = #{frameimgid}
and userid = #{selectuserid}
order by length(vtt_meta_json::TEXT) desc
limit 1

SELECT frameimgid
FROM vttm_frame_img_tb
WHERE videoid = #{videoid}
and shotid = #{shotid}
and assetfilename = #{assetimgfilename}
order by frameimgid desc
limit 1;
