mapper.insert:
INSERT INTO VTTM_COMPSERVERJOB_TB (
  COMPSERVERID, JOBNAME
) VALUES (
  #{compserverid}, #{jobname}
)

mapper.select:
SELECT workflowname, jobname, subtype, seq, pool, priority, expool FROM VTTM_WORKFLOWORDER_TB
AND WORKFLOWNAME = #{workflowname} AND SUBTYPE = #{subtype}
ORDER BY SEQ

SELECT A.*, (SELECT COUNT(*) FROM VTTM_COMPSERVER_TB WHERE STATUS > 0 AND CLIENTIP = A.CLIENTIP) AS WORKING FROM VTTM_COMPSERVER_TB A
POOL = #{pool} AND STATUS = 0 AND COMPSERVERID IN ( SELECT COMPSERVERID FROM VTTM_COMPSERVERJOB_TB
JOBNAME = #{jobname}
)
ORDER BY WORKING, TIMESTAMP LIMIT 1

mapper.insert:
insert into vttm_catalogque_tb
(videoid, catalog_path, video_full_path, status, callback_url, error_msg, progress, cancle_yn, update_date)
values
(
  #{videoid}
  ,#{catalog_path}
  ,#{video_full_path}
  ,'wait'
  ,#{callback_url}
  ,#{error_msg}
  ,#{progress}
  ,'N'
  ,now()
)on conflict (videoid)
do
update
set
  error_msg = #{error_msg},
  status = #{status},
  cancle_yn = #{cancle_yn},
  progress = ${progress},
  update_date = now()
